import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Header from '../../components/Header';

const DashboardInstructorProfile = () => {
  const [form, setForm] = useState({
    Instructor_id: '',
    Instructor_name: '',
    email: '',
    phone_number: '',
    department: '',
    faculty: '',
    position: '',
    profile_image: ''
  });

  const [selectedFile, setSelectedFile] = useState(null);
  const instructorId = localStorage.getItem('instructorId');

  useEffect(() => {
    if (instructorId) {
      axios.get(`http://localhost:5000/api/instructor/${instructorId}`)
        .then((res) => {
          const data = res.data;
          setForm({
            Instructor_id: data.Instructor_id || '',
            Instructor_name: data.Instructor_name || '',
            email: data.email || '',
            phone_number: data.phone_number || '',
            department: data.department || '',
            faculty: data.faculty || '',
            position: data.position || '',
            profile_image: data.profile_image || ''
          });
        })
        .catch((err) => {
          console.error('‚ùå ‡πÇ‡∏´‡∏•‡∏î‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏≠‡∏≤‡∏à‡∏≤‡∏£‡∏¢‡πå‡∏•‡πâ‡∏°‡πÄ‡∏´‡∏•‡∏ß:', err);
        });
    }
  }, [instructorId]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    setSelectedFile(file);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    let profileImageFilename = form.profile_image;

    if (selectedFile) {
      const formData = new FormData();
      formData.append('image', selectedFile);

      try {
        const res = await axios.post('http://localhost:5000/api/upload/profile-image', formData);
        profileImageFilename = res.data.filename;
      } catch (err) {
        console.error('‚ùå ‡∏≠‡∏±‡∏õ‡πÇ‡∏´‡∏•‡∏î‡∏£‡∏π‡∏õ‡πÑ‡∏°‡πà‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à:', err);
        alert('‡πÄ‡∏Å‡∏¥‡∏î‡∏Ç‡πâ‡∏≠‡∏ú‡∏¥‡∏î‡∏û‡∏•‡∏≤‡∏î‡πÉ‡∏ô‡∏Å‡∏≤‡∏£‡∏≠‡∏±‡∏õ‡πÇ‡∏´‡∏•‡∏î‡∏£‡∏π‡∏õ‡∏†‡∏≤‡∏û');
        return;
      }
    }

    const updatedForm = { ...form, profile_image: profileImageFilename };

    axios.put(`http://localhost:5000/api/instructor/${instructorId}`, updatedForm)
      .then(() => {
        localStorage.setItem('profile_image', profileImageFilename);
        alert('‚úÖ ‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÄ‡∏£‡∏µ‡∏¢‡∏ö‡∏£‡πâ‡∏≠‡∏¢‡πÅ‡∏•‡πâ‡∏ß');
      })
      .catch((err) => {
        console.error('‚ùå ‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏•‡πâ‡∏°‡πÄ‡∏´‡∏•‡∏ß:', err);
        alert('‡πÄ‡∏Å‡∏¥‡∏î‡∏Ç‡πâ‡∏≠‡∏ú‡∏¥‡∏î‡∏û‡∏•‡∏≤‡∏î‡πÉ‡∏ô‡∏Å‡∏≤‡∏£‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å');
      });
  };

  return (
    <div className="min-h-screen bg-[#9AE5F2] text-[#130347] font-sans">
      <Header />

      {/* ‡∏Ñ‡∏≠‡∏ô‡πÄ‡∏ó‡∏ô‡πÄ‡∏ô‡∏≠‡∏£‡πå‡∏†‡∏≤‡∏¢‡∏ô‡∏≠‡∏Å‡πÉ‡∏ä‡πâ‡πÄ‡∏õ‡∏≠‡∏£‡πå‡πÄ‡∏ã‡πá‡∏ô‡∏ï‡πå ‡πÉ‡∏´‡πâ‡∏´‡∏±‡∏ß‡πÄ‡∏£‡∏∑‡πà‡∏≠‡∏á‡∏≠‡∏¢‡∏π‡πà‡∏ô‡∏≠‡∏Å‡∏Å‡∏•‡πà‡∏≠‡∏á‡πÅ‡∏•‡∏∞‡∏ä‡∏¥‡∏î‡∏ã‡πâ‡∏≤‡∏¢‡∏ï‡∏£‡∏á‡∏Å‡∏±‡∏ö‡∏Å‡∏•‡πà‡∏≠‡∏á */}
      <div className="px-[5%] py-[2%]">
        <div className="w-full max-w-4xl mx-auto">
          {/* ‡∏´‡∏±‡∏ß‡πÄ‡∏£‡∏∑‡πà‡∏≠‡∏á (‡∏≠‡∏¢‡∏π‡πà‡∏ô‡∏≠‡∏Å‡∏Å‡∏•‡πà‡∏≠‡∏á) */}
          <h2 className="text-2xl font-extrabold mb-4">
            üë§ ‡πÇ‡∏õ‡∏£‡πÑ‡∏ü‡∏•‡πå‡∏≠‡∏≤‡∏à‡∏≤‡∏£‡∏¢‡πå‡∏õ‡∏£‡∏∞‡∏à‡∏≥‡∏ß‡∏¥‡∏ä‡∏≤
          </h2>

          {/* ‡∏Å‡∏•‡πà‡∏≠‡∏á‡∏ü‡∏≠‡∏£‡πå‡∏° */}
          <form
            onSubmit={handleSubmit}
            className="bg-white text-[#130347] p-8 rounded-2xl shadow-lg border border-[#E6F0FF]"
          >
            {/* ‡∏£‡∏π‡∏õ‡πÇ‡∏õ‡∏£‡πÑ‡∏ü‡∏•‡πå + ‡∏≠‡∏±‡∏õ‡πÇ‡∏´‡∏•‡∏î */}
            <div className="flex items-center gap-4 mb-6">
              <div className="w-24 h-24 rounded-full overflow-hidden ring-2 ring-[#E6F0FF] bg-[#F8FBFF] flex items-center justify-center">
                {form.profile_image ? (
                  <img
                    src={`http://localhost:5000/uploads/${form.profile_image}`}
                    alt="‡∏£‡∏π‡∏õ‡πÇ‡∏õ‡∏£‡πÑ‡∏ü‡∏•‡πå"
                    className="w-full h-full object-cover"
                  />
                ) : (
                  <span className="text-sm text-[#225EC4]">‡πÑ‡∏°‡πà‡∏°‡∏µ‡∏£‡∏π‡∏õ</span>
                )}
              </div>
              <div className="flex-1">
                <label className="block text-sm font-medium text-[#225EC4]">‡∏≠‡∏±‡∏õ‡πÇ‡∏´‡∏•‡∏î‡∏£‡∏π‡∏õ‡πÇ‡∏õ‡∏£‡πÑ‡∏ü‡∏•‡πå</label>
                <input
                  type="file"
                  accept="image/*"
                  onChange={handleImageUpload}
                  className="mt-1 block w-full text-sm
                             file:mr-3 file:rounded-full file:border-0
                             file:bg-[#6EC7E2] file:text-white file:px-4 file:py-2
                             hover:file:bg-[#4691D3]"
                />
              </div>
            </div>

            {/* ‡∏ü‡∏¥‡∏•‡∏î‡πå‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏• */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="block text-sm font-medium text-[#225EC4]">‡∏£‡∏´‡∏±‡∏™‡∏≠‡∏≤‡∏à‡∏≤‡∏£‡∏¢‡πå</label>
                <input
                  name="Instructor_id"
                  value={form.Instructor_id}
                  disabled
                  className="w-full rounded-xl border border-[#E6F0FF] bg-gray-100 px-3 py-2"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-[#225EC4]">‡∏ä‡∏∑‡πà‡∏≠</label>
                <input
                  name="Instructor_name"
                  value={form.Instructor_name}
                  onChange={handleChange}
                  required
                  className="w-full rounded-xl border border-[#E6F0FF] bg-[#F8FBFF] px-3 py-2 outline-none focus:ring-2 focus:ring-[#6EC7E2]"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-[#225EC4]">‡∏≠‡∏µ‡πÄ‡∏°‡∏•</label>
                <input
                  type="email"
                  name="email"
                  value={form.email}
                  onChange={handleChange}
                  required
                  className="w-full rounded-xl border border-[#E6F0FF] bg-[#F8FBFF] px-3 py-2 outline-none focus:ring-2 focus:ring-[#6EC7E2]"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-[#225EC4]">‡πÄ‡∏ö‡∏≠‡∏£‡πå‡πÇ‡∏ó‡∏£</label>
                <input
                  name="phone_number"
                  value={form.phone_number}
                  onChange={handleChange}
                  className="w-full rounded-xl border border-[#E6F0FF] bg-[#F8FBFF] px-3 py-2 outline-none focus:ring-2 focus:ring-[#6EC7E2]"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-[#225EC4]">‡∏†‡∏≤‡∏Ñ‡∏ß‡∏¥‡∏ä‡∏≤</label>
                <input
                  name="department"
                  value={form.department}
                  onChange={handleChange}
                  className="w-full rounded-xl border border-[#E6F0FF] bg-[#F8FBFF] px-3 py-2 outline-none focus:ring-2 focus:ring-[#6EC7E2]"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-[#225EC4]">‡∏Ñ‡∏ì‡∏∞</label>
                <input
                  name="faculty"
                  value={form.faculty}
                  onChange={handleChange}
                  className="w-full rounded-xl border border-[#E6F0FF] bg-[#F8FBFF] px-3 py-2 outline-none focus:ring-2 focus:ring-[#6EC7E2]"
                />
              </div>

              <div className="md:col-span-2">
                <label className="block text-sm font-medium text-[#225EC4]">‡∏ï‡∏≥‡πÅ‡∏´‡∏ô‡πà‡∏á</label>
                <input
                  name="position"
                  value={form.position}
                  onChange={handleChange}
                  className="w-full rounded-xl border border-[#E6F0FF] bg-[#F8FBFF] px-3 py-2 outline-none focus:ring-2 focus:ring-[#6EC7E2]"
                />
              </div>
            </div>

            {/* ‡∏õ‡∏∏‡πà‡∏°‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å */}
            <button
              type="submit"
              className="mt-6 w-full rounded-full bg-[#225EC4] hover:bg-[#1b55b5] text-white py-3 font-semibold shadow-sm"
            >
              üíæ ‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default DashboardInstructorProfile;
